sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

	//var gdt = new GlideDateTime().getDisplayValue();  
  	gdt = new Date();
  	var Year = gdt.getFullYear();
    var Month = gdt.getMonth() + 1;
    var Day = gdt.getDay();
    var Hour = gdt.getHours();
    var Minute = gdt.getMinutes();
    var Second = gdt.getSeconds();
  	outputs.folder_name = 'TMP' + Year + ('0' + Month).slice(-2) + ('0' + Day).slice(-2) + ('0' + Hour).slice(-2) + ('0' + Minute).slice(-2) + ('0' + Second).slice(-2);

})(inputs, outputs);

