sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  if ( inputs.resp_status == '201' ) {

    outputs.status = true; 

    //フォルダ作成のレスポンスから、フォルダIDを取得し次のactionにわたす。
    var resp_body = JSON.parse(inputs.resp_body);
    outputs.folder_id = resp_body.id;

  } else {

    outputs.status = false;

  }

})(inputs, outputs);

