sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
	if ( inputs.resp_status == '200' ) {

      outputs.status = true;

 	  var resp_body = JSON.parse(inputs.resp_body);
      outputs.link_url = resp_body.shared_link.url;

    } else {

      outputs.status = false;

    }

})(inputs, outputs);

