sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  if ( inputs.resp_status == '201' ) {

    outputs.status = true;

    var resp_body = JSON.parse(inputs.resp_body);
    for (var i=0; i<resp_body.entries.length; i++ ){
		if (resp_body.entries[i].type !== 'file') {
        	continue;
        }
      	outputs.file_id = resp_body.entries[i].id;
    }

  } else {

  	outputs.status = false;

  }

})(inputs, outputs);

